func timSoChanCuoiCung(arr: [Int]) -> Int? {
    var resultArr: [Int] = []
    for item in arr {
        if(item % 2 == 0) {
            resultArr.append(item)
        }
    }

    if(resultArr.count > 0){
        return resultArr.last
    }
    return -1
}
