//143.Viết hàm tìm “số chẵn đầu tiên” trong mảng các số nguyên (chandau). Nếu mảng không có giá trị chẵn thì hàm sẽ trả về giá trị không chẵn là - 1.
func soChanDauTien(arr: [Int]) -> Int {
    return arr.first(where: {$0 % 2 == 0}) ?? -1
}

//print("So Chan dau tien: \(soChanDauTien(arr: intArr))")

//144.Tìm “số nguyên tố đầu tiên” trong mảng một chiều các số nguyên (nguyentodau). Nếu mảng không có số nguyên tố thì trả về giá trị -1.
func kiemTraSoNguyenTo(number: Int) -> Bool{
    if(number < 2){
        return true
    }
    let arrNumber = Array(2...number)
    for i in arrNumber {
        if(number % i == 0)
        {
            return false
        }
    }
    return true
}
func soNguyenToDauTien(arr: [Int]) -> Int{
    return arr.first(where: {kiemTraSoNguyenTo(number: $0) == true}) ?? -1
}
print("So Nguyen To Dau Tien Trong Mang: \(soNguyenToDauTien(arr: intArr))")
